import { Observable } from 'rxjs';
import { User } from './../interfaces/user';
import { AuthService } from 'src/app/servicios/auth.service';
import { Injectable } from '@angular/core';
import { AngularFirestore } from '@angular/fire/compat/firestore';
import { Firestore } from '@angular/fire/firestore';
import { addDoc, collection } from 'firebase/firestore';

@Injectable({
  providedIn: 'root'
})
export class ResultadosService {
  private user!: User;
  private u: any;

  constructor(private afs: AngularFirestore,private auth: AuthService) {
    this.u= this.miUser();

    this.user.displayName=this.u.displayName;
    this.user.email= this.u.email;

    console.log(this.user.displayName);
   }

async miUser() {
  return await this.auth.getUserLog().then((res)=>{console.log(res)});
}


  async guardarResultadoAhorcado(puntaje: number)
  {
    try {
      const docRef = await addDoc(collection(this.afs.firestore,"Resultados/ahorcado"), {
        puntaje: puntaje,
        nombre: this.user.displayName,
        correo: this.user.email,
        fecha: new Date(),
      });
      console.log("Resultado registado en ahorcado: ", docRef.id);

    } catch (e) {
      console.error("Error al agregar document: ", e);

    }
  }
}
