import { User } from './../interfaces/user';
import { AuthService } from 'src/app/servicios/auth.service';
import { Injectable } from '@angular/core';
import { AngularFirestore } from '@angular/fire/compat/firestore';
import { Firestore } from '@angular/fire/firestore';
import { addDoc, collection, doc, updateDoc } from 'firebase/firestore';
import { AngularFireAuth } from '@angular/fire/compat/auth';

@Injectable({
  providedIn: 'root'
})
export class ResultadosService {
  private user: any={};

  constructor(private afs: AngularFirestore,private afAuth: AngularFireAuth) {
    this.afAuth.authState.subscribe(user=>{
      if(!user)
      {
        return ;
      }

        this.user.displayName= user.displayName;
        this.user.email= user.email;
        this.user.photoUrl= user.photoURL;
        this.user.uid= user.uid;


      console.log(this.user);
      });

   }

  async guardarResultadoAhorcado(puntaje: number)
  {
    try {
      const docRef = await addDoc(collection(this.afs.firestore,"Ahorcado"), {
        puntaje: puntaje,
        correo: this.user.email,
        nombre: this.user.displayName,
        uid: this.user.uid,
        fecha: new Date(),
      });
      console.log("Documento escrito en colección: ", docRef.id);
      this.user.uid=docRef.id;
    } catch (e) {
      console.error("Error al agregar document: ", e);

    }
  }

  async actualizarResultadoAhorcado(puntaje: number) {
    const placeRef = doc(this.afs.firestore,'Ahorcado',this.user.email);
    await updateDoc(placeRef, { puntaje: puntaje } ).then(res=>{
      console.log(res);
    })
  }



}
