import { Usuario } from 'src/app/clases/usuario';
import { AngularFireAuth } from '@angular/fire/compat/auth';
import { AuthService } from './../servicios/auth.service';
import { Injectable } from '@angular/core';
import { ActivatedRouteSnapshot, CanActivate, RouterStateSnapshot, UrlTree } from '@angular/router';
import { Observable } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class GuardAdministradorGuard implements CanActivate {

  public usuarios!: Usuario[];
  constructor(private auth: AuthService, private afAuth: AngularFireAuth) {

  }

  async canActivate(route: ActivatedRouteSnapshot, state: RouterStateSnapshot): Promise<boolean>
  {
    let admin=false;

    const user= await this.auth.getUserLog();

    this.auth.traerUsuarios().forEach(usu=>{
      this.usuarios=usu;
      alert('aca se entro?');
      console.log(this.usuarios);
    });

    if(user)
    {
        for(let item of this.usuarios)
        {
          if(item.uid==user.uid)
          {
            if(item.perfil=='administrador')
            {
              admin=true;
              break;
            }
          }
        }
    }

      return admin;
  }

}
